mapper.select:
SELECT count(u.user_id)
FROM user u
WHERE u.user_id = #{userId}
  AND u.user_password = #{userPassword}

SELECT COUNT(U.USER_ID)
FROM USER U
WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO USER ( USER_ID
                 , USER_PASSWORD
                 , USER_NAME
                 , PHONE_NUMBER
                 , USER_YN)
VALUES ( #{userId}
       , #{userPassword}
       , #{userName}
       , #{phoneNumber}
       , 'n')
